const raw = $json.line_items;

return raw.map(item => ({
  json: {
    order_id: item.order_id,
    product_id: item.order_item_id,
    quantity: item.quantity || 1,
    amount: parseFloat(item.amount) / 100,
    currency: 'USD',
    product_name: item.name
  }
}));

const raw = $json.line_items;
return raw.map(item => ({
  json: {
    order_id: item.order_id,
    quantity: item.quantity,
    amount: parseFloat(item.products_price.amount),
    currency: item.products_price.currency,
    product_id: item.original_product.id,
    product_name: item.original_product.name,
    variant_id: item.products_variant.id,
    variant_name: item.products_variant.name
  }
}));
